using System.Text.Json.Serialization;

namespace DNA;

class Program
{
    static void Main(string[] args)
    {
        Solution.FindRepeatedDnaSequences("AAAAACCCCCAAAAACCCCCCAAAAAGGGTTT");
        Solution.FindRepeatedDnaSequences("AAAAAAAAAAAAA");
        Solution.FindRepeatedDnaSequences("AAAAAAAAAA");
        // Solution.FindRepeatedDnaSequences("");
    }

    public static class Solution
    {
        public static IList<string> FindRepeatedDnaSequences(string s)
        {
            HashSet<string> result = [];
            IList<string> seen = [];
            string looking = "";

            for (int left = 0; left < s.Length; left++)
            {
                if (s.Length - left >= 10) looking = s.Substring(left, 10);

                if (looking.Length == 10 && seen.Contains(looking)) result.Add(looking); else seen.Add(looking);
                looking = "";
            }

            Console.WriteLine($"Result: ");
            foreach (var item in result)
            {
                Console.WriteLine($"[{item} ]");
            }

            Console.WriteLine($"Seen: ");
            foreach (var item in seen)
            {
                Console.WriteLine($"[{item} ]");
            }

            return result.ToArray();
        }
    }
}